// src/components/Carrito/FloatingCart.jsx
import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import CartSidebar from "./CartSidebar"; // Asegúrate que CartSidebar esté separado

export default function FloatingCart() {
    const [cartVisible, setCartVisible] = useState(false);
    const [carrito, setCarrito] = useState([
        {
            producto: {
                nombre: "Carne de Res",
                stock: 10,
                precioUnitario: 15000,
                imagen: "https://via.placeholder.com/64"
            },
            cantidad: 1
        }
    ]);

    const updateQuantity = (index, cantidad) => {
        const updated = [...carrito];
        updated[index].cantidad = cantidad;
        setCarrito(updated);
    };

    const removeItem = (index) => {
        const updated = [...carrito];
        updated.splice(index, 1);
        setCarrito(updated);
    };

    const checkout = () => {
        alert("Realizando checkout...");
        setCarrito([]);
        setCartVisible(false);
    };

    return (
        <>
            <button 
                className="button is-primary is-rounded"
                style={{
                    position: "fixed",
                    top: "20px",
                    left: "20px",
                    zIndex: 2000
                }}
                onClick={() => setCartVisible(true)}
            >
                <FontAwesomeIcon icon={faShoppingCart} />
            </button>

            <CartSidebar
                visible={cartVisible}
                onClose={() => setCartVisible(false)}
                cartItems={carrito}
                updateQuantity={updateQuantity}
                removeItem={removeItem}
                checkout={checkout}
            />
        </>
    );
}
