import React from 'react';
import 'bulma/css/bulma.min.css';
import { useParams } from 'react-router-dom';
import Titulo from '../Titulo/Titulo';


const productos = {
  res: [
    { id: 1, nombre: 'Carne molida', precio: '$15.000/kg', imagen: 'https://www.merinosoto.com/wp-content/uploads/2020/02/cortes-carne-distribuidor-de-carnes-jaen-1024x682.jpg' },
    { id: 2, nombre: 'Costilla de res', precio: '$18.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 3, nombre: 'Chatas', precio: '$22.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 4, nombre: 'Bife de chorizo', precio: '$25.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 5, nombre: 'Asado de tira', precio: '$30.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 6, nombre: 'Entraña', precio: '$27.000/kg', imagen: 'https://via.placeholder.com/150' },
  ],
  pollo: [
    { id: 1, nombre: 'Pechuga de pollo', precio: '$12.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 2, nombre: 'Muslo de pollo', precio: '$10.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 3, nombre: 'Alas de pollo', precio: '$8.000/kg', imagen: 'https://via.placeholder.com/150' },
  ],
  cerdo: [
    { id: 1, nombre: 'Costillas de cerdo', precio: '$20.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 2, nombre: 'Lomo de cerdo', precio: '$18.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 3, nombre: 'Chuletas de cerdo', precio: '$22.000/kg', imagen: 'https://via.placeholder.com/150' },
  ],
  pescado: [
    { id: 1, nombre: 'Salmón', precio: '$40.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 2, nombre: 'Trucha', precio: '$35.000/kg', imagen: 'https://via.placeholder.com/150' },
    { id: 3, nombre: 'Atún', precio: '$50.000/kg', imagen: 'https://via.placeholder.com/150' },
  ],
};

export default function Productos() {
  const { categoria } = useParams();
  // Filtrar productos según la categoría seleccionada
  const productosCategoria = productos[categoria] || [];

  return (
    <div>
        <Titulo /> {/* Aquí se incluye el componente Titulo */}
        <section className="section">
        <div className="container">
            <h1 className="title has-text-centered">{`Productos de ${categoria.charAt(0).toUpperCase() + categoria.slice(1)}`}</h1>
            <div className="columns is-multiline">
            {productosCategoria.map((producto) => (
                <div className="column is-one-third" key={producto.id}>
                <div className="card">
                    <div className="card-image">
                    <figure className="image is-4by3">
                        <img src={producto.imagen} alt={producto.nombre} />
                    </figure>
                    </div>
                    <div className="card-content">
                    <p className="title is-5">{producto.nombre}</p>
                    <p className="subtitle is-6">{producto.precio}</p>
                    </div>
                </div>
                </div>
            ))}
            </div>
        </div>
        </section>
    </div>
  );
}
